  render() {
    return (
      <Container>
        <footer className={styles.footer}>
          <div className={styles.created}>
            Created by
            <Image size='small' src={logo}/>
          </div>
          <div className={styles.copyright}>Copyright Â© 2021 Polygon technology</div>
        </footer>
      </Container>
    )
  }
}
